export async function cargarParrafo(){try{const r=await fetch("/api/parrafo");if(!r.ok)throw new Error(`HTTP error! status: ${r.status}`);return await r.json()}catch(r){throw r}}export async function cargarImagenes(){try{const r=await fetch("/api/imagenes");if(!r.ok)throw new Error(`HTTP error! status: ${r.status}`);return await r.json()}catch(r){throw r}}export async function guardarComentario(r,t){try{const a=await fetch("/api/guardarComentario",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({usuario:r,textoComentario:t})});if(!a.ok)throw new Error(`HTTP error! status: ${a.status}`);return await a.json()}catch(r){throw r}}export async function cargarComentarios(){try{const r=await fetch("/api/cargarComentarios");if(!r.ok)throw new Error(`HTTP error! status: ${r.status}`);return await r.json()}catch(r){throw r}}